// eslint-disable-next-line no-unused-vars
import { motion, AnimatePresence } from 'framer-motion';
import { useEffect } from 'react';
import { Howl } from 'howler';
import PropTypes from 'prop-types';

// Base64 encoded wav audio so binary assets aren't required in repo
const successBeep =
  '';
const dangerBeep =
  '';

const successSound = new Howl({ src: [`data:audio/wav;base64,${successBeep}`] });
const dangerSound = new Howl({ src: [`data:audio/wav;base64,${dangerBeep}`] });

export default function FraudPopup({ isOpen, onClose, result }) {
  useEffect(() => {
    if (isOpen && result) {
      if (result.fraud) {
        dangerSound.play();
      } else {
        successSound.play();
      }
    }
  }, [isOpen, result]);

  if (!result) return null;

  const borderColor = result.fraud ? 'border-red-600' : 'border-green-600';
  const explanation = result.fraud
    ? 'Fraud suspected due to high-risk medication or excessive quantity.'
    : 'No anomalies detected in patient history or prescription pattern.';
  const medicationRisk =
    result.medication_risk &&
    result.medication_risk.charAt(0).toUpperCase() +
      result.medication_risk.slice(1);

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          className="fixed inset-0 z-50 flex items-center justify-center"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          <div
            className="absolute inset-0 bg-black bg-opacity-60"
            onClick={onClose}
          />
          <motion.div
            onClick={(e) => e.stopPropagation()}
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.8, opacity: 0 }}
            className={`relative z-10 bg-white dark:bg-gray-800 rounded-xl shadow-xl border-4 ${borderColor} p-6 w-[90%] md:w-[400px]`}
          >
            <h2 className="text-xl font-semibold mb-4">ðŸ§  AI Fraud Check Result</h2>
            <div className="space-y-1 mb-4">
              <p className="font-medium">Risk Score: {result.risk_score}</p>
              <p>Medication Risk: {medicationRisk}</p>
              <p>Fraudulent: {result.fraud ? 'Yes' : 'No'}</p>
            </div>
            <p className="mb-6">{explanation}</p>
            <div className="flex justify-end gap-3">
              <button
                className="px-3 py-2 rounded-md bg-blue-600 text-white hover:bg-blue-500"
                onClick={onClose}
              >
                ! Proceed with Caution
              </button>
              <button
                className="px-3 py-2 rounded-md bg-gray-700 text-white hover:bg-gray-600"
                onClick={onClose}
              >
                âœ– Close
              </button>
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

FraudPopup.propTypes = {
  isOpen: PropTypes.bool.isRequired,
  onClose: PropTypes.func.isRequired,
  result: PropTypes.object,
};
